/**
 * This script generates metadata for Eclipse packaging.
 * Generated files: apack_zap.package
 */

// apack_zap.package
const fs = require('fs')
const path = require('path')

const packageJson = path.join(__dirname, '../package.json')
const zapPackage = path.join(__dirname, '../apack_zap.package')

fs.readFile(packageJson, 'utf8', (err, data) => {
  if (err) {
    console.log(`Unable to read from package.json:`, err)
    return
  }
  let json = JSON.parse(data)
  let version = json.version
  const template = `<?xml version="1.0" encoding="UTF-8"?>
<!-- This file is generated by ZAP. Do not manually edit this file! -->
<package id="com.silabs.apack.zap.feature"
  label="Adapter Pack for ZCL Advanced Platform"
  description="Provides a runtime interface to ZCL Advanced Platform"
  changelog="releasenotes.md"
  types="apack"
  partCompatibility="mcu.arm.efr32.mg.*"
  version="${version}"/>
`

  console.log(`ğŸ Generating ${zapPackage} with version number ${version}`)
  fs.writeFileSync(zapPackage, template)
})
